// n8n webhook may put POST body in $json.body or at root level
const data = $json.body || $json;

// Get integration from _integrations object (keyed by integration type)
const integrations = data._integrations || {};
const integration = integrations.booking_api;

if (!data.date || !data.time) {
  return {
    _error: true,
    success: false,
    message: 'Date and time are required for booking. Please provide when you would like to make a reservation.'
  };
}

if (!integration || !integration.apiUrl) {
  return {
    _error: true,
    success: false,
    message: 'No API integration configured for this client. Please set up a booking_api integration in the admin panel.',
    debug: { hasIntegrations: !!data._integrations, keys: Object.keys(integrations) }
  };
}

// Use full URL from integration (no path appending needed)
let apiUrl = integration.apiUrl;

// Prepare API call config
return {
  date: data.date,
  time: data.time,
  serviceType: data.serviceType || 'Table Reservation',
  customerName: data.customerName || 'Guest',
  customerEmail: data.customerEmail || '',
  customerPhone: data.customerPhone || '',
  partySize: data.partySize || 2,
  notes: data.notes || '',
  _apiConfig: {
    url: apiUrl,
    key: integration.apiKey,
    method: integration.method || 'POST',
    authMethod: integration.authMethod || 'bearer'
  }
};

// Extract response from HTTP Request node
// n8n HTTP Request with fullResponse: true returns { statusCode, body, headers, ... }
// With neverError: true, we get the response even for error status codes
const response = $json.body || $json;
const statusCode = $json.statusCode || $json.status || 200;

// Check if this is an error response (4xx or 5xx)
if (statusCode >= 400) {
  // For error responses, return the error message from the API
  // The API should return { success: false, error: '...', message: '...' }
  return {
    success: false,
    error: response.error || response.message || `API returned status ${statusCode}`,
    message: response.message || response.error || `Request failed with status ${statusCode}`,
    statusCode: statusCode,
    ...(response.availableTimes && { availableTimes: response.availableTimes }),
    ...(response.data && { data: response.data })
  };
}

// For successful responses (2xx), return the response as-is
return response;
