// n8n webhook may put POST body in $json.body or at root level
const data = $json.body || $json;

// Validate required parameters
if (!data.productName && !data.productSku) {
  return {
    _error: true,
    success: false,
    message: 'Please provide either productName or productSku to check inventory.',
    requiredParameters: {
      productName: { type: 'string', description: 'Product name to search for (e.g., "pepperoni pizza")' },
      productSku: { type: 'string', description: 'Product SKU code (e.g., "PIZZA-001")' },
      quantity: { type: 'number', description: 'Quantity to check availability for (optional, default: 1)' }
    },
    example: { productName: 'pepperoni pizza', quantity: 2 }
  };
}

// Get integration from _integrations object (keyed by integration type)
const integrations = data._integrations || {};
const integration = integrations.inventory_api;

if (!integration || !integration.apiUrl) {
  return {
    _error: true,
    success: false,
    message: 'No API integration configured for this client. Please set up an inventory_api integration in the admin panel.',
    debug: { hasIntegrations: !!data._integrations, keys: Object.keys(integrations) }
  };
}

// Use full URL from integration (no path appending needed)
// Replace any placeholders if present
let apiUrl = integration.apiUrl;
apiUrl = apiUrl.replace('{productName}', encodeURIComponent(data.productName || ''));
apiUrl = apiUrl.replace('{productSku}', encodeURIComponent(data.productSku || ''));

// Prepare API call config
return {
  productName: data.productName,
  productSku: data.productSku,
  quantity: data.quantity || 1,
  _apiConfig: {
    url: apiUrl,
    key: integration.apiKey,
    method: integration.method || 'POST',
    authMethod: integration.authMethod || 'bearer'
  }
};
